const p=t=>{const e=document.getElementById("product-card").content,o=e.querySelector(".link");o.href=`details.html?id=${t.id}`;const l=e.querySelector(".image");l.src=t.url;const s=e.querySelector(".enteprise");s.textContent=t.enterprise;const a=e.querySelector(".name");a.textContent=t.name;const i=e.querySelector(".price");i.textContent=`$${t.price}`;const n=e.querySelector(".rating");n.replaceChildren();for(let c=0;c<t.reviews;c++){const r=document.createElement("img");r.src=`${new URL("/assets/estrella.f66d2821.png",self.location).href}`,r.alt="Star",r.classList.add("w-3"),n.appendChild(r)}return e.cloneNode(!0)};export{p as c};
