const m=t=>{const e=document.getElementById("product-card").content,o=e.querySelector(".link");o.href=`details.html?id=${t.id}`;const l=e.querySelector(".image");l.src=t.url;const a=e.querySelector(".enteprise");a.textContent=t.enterprise;const s=e.querySelector(".name");s.textContent=t.name;const i=e.querySelector(".price");i.textContent=`$${t.price}`;const n=e.querySelector(".rating");n.replaceChildren();for(let c=0;c<t.reviews;c++){const r=document.createElement("img");r.src="img/estrella.png",r.alt="Star",r.classList.add("w-3"),n.appendChild(r)}return e.cloneNode(!0)};export{m as c};
